import { Address, Hex } from "viem"

export type SafeDeploymentTransaction = {
  to: Address
  data: Hex
}
export interface OrangeKitSafeFactory {
  predictAddress(truncatedBitcoinAddress: string): Promise<Hex>
  populateSafeDeploymentTransaction(
    truncatedBitcoinAddress: Hex,
  ): Promise<SafeDeploymentTransaction>
  simulateSafeDeploymentTransaction(
    truncatedBitcoinAddress: Hex,
    from: Address,
  ): Promise<boolean>
}
