export type TransactionSenderRequest = {
  to: `0x${string}`
  data: `0x${string}`
  chainId: number
}
export type TransactionSenderResponse = {
  hash: `0x${string}`
}

export interface TransactionSender {
  refundReceiver?: string
  sendTransaction(
    tx: TransactionSenderRequest,
  ): Promise<TransactionSenderResponse>
}
