import { useCallback } from "react"
import { Config } from "wagmi"
import {
  EventCallback,
  useSubscribeToConnectorEvent,
} from "./useSubscribeToConnectorEvent"
import {
  isUnsupportedBitcoinAddressError,
  BitcoinWalletConnectionError,
} from "../wallet/errors"

function useSubscribeToConnectedToUnsupportedBitcoinAddress(
  config: Config,
  connectorId: string,
  errorCallback: EventCallback<"error">,
) {
  const callback = useCallback<EventCallback<"error">>(
    (data) => {
      const { error } = data
      if (!(error instanceof BitcoinWalletConnectionError)) return

      const { cause } = error

      if (isUnsupportedBitcoinAddressError(cause)) errorCallback(data)
    },
    [errorCallback],
  )

  useSubscribeToConnectorEvent(config, connectorId, "error", callback)
}

export { useSubscribeToConnectedToUnsupportedBitcoinAddress }
