/* eslint-disable max-classes-per-file */
import { UnsupportedBitcoinAddressError } from "@mezo-org/orangekit-smart-account"

export class BitcoinWalletConnectionError extends Error {
  constructor(walletName: string, cause: Error) {
    super(`Failed to connect ${walletName} Bitcoin wallet`, { cause })
  }
}

export class WalletNetworkDoesNotMatchProviderChainError extends Error {
  constructor() {
    const message =
      "Wallet network does not match provider id. Make sure you use proper network in your wallet."
    super(message)
  }
}

export function isUnsupportedBitcoinAddressError(error: unknown) {
  return error instanceof UnsupportedBitcoinAddressError
}

export function isWalletNetworkDoesNotMatchProviderChainError(error: unknown) {
  return error instanceof WalletNetworkDoesNotMatchProviderChainError
}
