/* eslint-disable import/prefer-default-export */
import { CreateWalletFn } from "../bitcoin-wallet-provider"
import { getOrangeKitOKXConnector } from "./connector"
import { ConnectorConfig } from "../connector"

export function getOKXWallet(config: ConnectorConfig): CreateWalletFn {
  return () => ({
    id: "orangekit-okx",
    name: "OrangeKit OKX",
    iconUrl: "https://www.svgrepo.com/show/74027/orange.svg",
    iconAccent: "#1E1E1E",
    iconBackground: "#1E1E1E",
    installed: !!window.okxwallet?.bitcoin,
    createConnector: getOrangeKitOKXConnector(config),
    downloadUrls: {
      desktop:
        "https://chromewebstore.google.com/detail/okx-wallet/mcohilncbfahbmgdjkbpemcciiolgcge",
    },
  })
}
