/* eslint-disable import/prefer-default-export */
import { CreateWalletFn } from "../bitcoin-wallet-provider"
import { getOrangeKitUnisatConnector } from "./connector"
import { ConnectorConfig } from "../connector"

export function getUnisatWallet(config: ConnectorConfig): CreateWalletFn {
  return () => ({
    id: "orangekit-unisat",
    name: "OrangeKit Unisat",
    iconUrl: "https://www.svgrepo.com/show/74027/orange.svg",
    iconAccent: "#1E1E1E",
    iconBackground: "#1E1E1E",
    installed: !!window.unisat,
    createConnector: getOrangeKitUnisatConnector(config),
    downloadUrls: {
      desktop:
        "https://chromewebstore.google.com/detail/unisat-wallet/ppbibelpcjmhbdihakflkdcoccbgbkpo",
    },
  })
}
